    private void jButton1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton1MouseClicked
        // TODO add your handling code here:
        if(jTextField_nombres.getText().length() < 1)
          JOptionPane.showMessageDialog(null,"El campo Nombre esta vacio");    
        if(jTextField_apellidos.getText().length() < 1)
          JOptionPane.showMessageDialog(null,"El campo apellido esta vacio");    
        if(jTextField_ocupacion.getText().length() < 1)
          JOptionPane.showMessageDialog(null,"El campo ocupacion esta vacio");    
        if(jTextField_telefono_celular.getText().length() < 1)
          JOptionPane.showMessageDialog(null,"El campo celular esta vacio");    
        if(jTextField_email.getText().length() < 1)
          JOptionPane.showMessageDialog(null,"El campo correo esta vacio"); 
        if(jTextField_matricula.getText().length() < 1)
          JOptionPane.showMessageDialog(null,"El campo matricula esta vacio");    
        if(jTextField_balance.getText().length() < 1)
          JOptionPane.showMessageDialog(null,"El campo balance esta vacio");    
        
    }//GEN-LAST:event_jButton1MouseClicked

