    private void jButton_cerrarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton_cerrarMouseClicked
        // TODO add your handling code here:
         beep();
        int valor = JOptionPane.showConfirmDialog(this,"Esta seguro que desea salir?","Advertencia",JOptionPane.YES_NO_OPTION);
        if(valor == JOptionPane.YES_OPTION){
            
        System.exit(0);
        }
    }//GEN-LAST:event_jButton_cerrarMouseClicked

    private void jTextField_balanceKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTextField_balanceKeyTyped
       char caracter = evt.getKeyChar();
        if((caracter<'0' || caracter>'9') && (caracter > '.' || caracter < ',')){
            evt.consume();//eliminar lo que no este dentro de la condicion
            beep();
        } 
            
    }//GEN-LAST:event_jTextField_balanceKeyTyped

